// CALCULATE FREE SLOTS (FIXED - proper event duration handling)
const events = $input.all();
const dateStr = $('Normalize Input').item.json.date;
const toolCallId = $('Normalize Input').item.json.tool_call_id;

const workStart = 9;
const workEnd = 18;
const allSlots = [];

// Generate all 30-minute slots
for (let hour = workStart; hour < workEnd; hour++) {
  for (let minute of [0, 30]) {
    if (hour === 17 && minute === 30) break; // Last slot is 17:00

    const timeStr = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
    const timeInMinutes = hour * 60 + minute;

    allSlots.push({
      time: timeStr,
      minutes: timeInMinutes,
      available: true
    });
  }
}

// Mark busy slots based on existing events
events.forEach(event => {
  const eventData = event.json;
  if (!eventData.start?.dateTime || !eventData.end?.dateTime) return;

  // Parse event START time
  const startTimePart = eventData.start.dateTime.split('T')[1].split('+')[0].split('-')[0];
  const [startHour, startMinute] = startTimePart.split(':').map(Number);
  const eventStartMinutes = startHour * 60 + startMinute;

  // Parse event END time
  const endTimePart = eventData.end.dateTime.split('T')[1].split('+')[0].split('-')[0];
  const [endHour, endMinute] = endTimePart.split(':').map(Number);
  const eventEndMinutes = endHour * 60 + endMinute;

  // Mark ALL slots that fall within the event's time range
  allSlots.forEach(slot => {
    if (slot.minutes >= eventStartMinutes && slot.minutes < eventEndMinutes) {
      slot.available = false;
    }
  });
});

const freeSlots = allSlots.filter(s => s.available).map(s => s.time);

// Format message for voice (numbers as words)
const timeMapping = {
  '09:00': 'nouă', '09:30': 'nouă și treizeci',
  '10:00': 'zece', '10:30': 'zece și treizeci',
  '11:00': 'unsprezece', '11:30': 'unsprezece și treizeci',
  '12:00': 'douăsprezece', '12:30': 'douăsprezece și treizeci',
  '13:00': 'treisprezece', '13:30': 'treisprezece și treizeci',
  '14:00': 'paisprezece', '14:30': 'paisprezece și treizeci',
  '15:00': 'cincisprezece', '15:30': 'cincisprezece și treizeci',
  '16:00': 'șaisprezece', '16:30': 'șaisprezece și treizeci',
  '17:00': 'șaptesprezece'
};

const voiceSlots = freeSlots.map(time => timeMapping[time] || time);

let message;
if (freeSlots.length === 0) {
  message = `Pentru ${dateStr} nu mai avem slot-uri disponibile. Alegeți altă zi.`;
} else {
  // Oferă doar primele 5 slot-uri ca exemple
  const firstFiveSlots = voiceSlots.slice(0, 5);
  message = `Pentru ${dateStr} avem ${freeSlots.length} slot-uri disponibile. Exemple: ${firstFiveSlots.join(', ')} și alte ore.`;
}

return {
  json: {
    results: [
      {
        toolCallId: toolCallId,
        result: message
      }
    ],
    date: dateStr,
    free_slots: freeSlots,
    total_free: freeSlots.length
  }
};
